import styled from 'styled-components';

export const Statistics = styled.section`
  width: 330px;
  margin: 20px auto;
  border-radius: 5px;
  background-color: rgba(108, 107, 114, 0.493);
  text-align: center;
  padding: 5px 0px 0px 0px;
`;
